package StaticPackage_13_12_2025;

public class Static1 {
	
	static int id;
	static String name;
	static int salary;
	
	public static void AXisBank() {
		System.out.println("My id is " + id);
	}
	
	public static void BankofIndia() {
		System.out.println("My name is " + name);
	}
	
	public static void PunjabBank() {
		System.out.println("My salary is " + salary);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Static1.id=22;
		Static1.name="SBIBANK";
		Static1.salary=60000;
		
		Static1.AXisBank();
		Static1.BankofIndia();
		Static1.PunjabBank();
		
		

	}

}
