public class Factorial_1 {

	private static int num;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sum = 5;
		int fact = 1;
		
		for (int i = 1; i <= num; i++) {
			
			fact = fact * i;
			
		}
		
		System.out.println("Factorial is : " + fact);

	}

}
