package MethodA_12_12_2025;

public class Method1 {
	
	public static void add(int i1, int i2) {
		int sum=i1+i2;
		System.out.println(sum);
	}
	
	public static void sub(int i3, int i4) {
		int sum=i3-i4;
		System.out.println(sum);
	}
	
	public static void mul(int i5, int i6) {
		int sum=i5*i6;
		System.out.println(sum);
	}
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Method1.add(1000, 2000);
		Method1.sub(2000, 1000);
		Method1.mul(1000,500);

	}

}
