package MethodArgument_05_11_2025;

public class MethodClass1 {
	
	public static void Add(int a1, int b2) {
		int sum=a1+b2;
		System.out.println(sum);
		
	}
	
	public static void Sub(int c3, int d4) {
		int sum=c3-d4;
		System.out.println(sum);
		
	}
	public static void Mul(int d5, int e6) {
		int sum=d5*e6;
		System.out.println(sum);
	}
     
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		MethodClass1.Add(200, 300);
		MethodClass1.Sub(100, 50);
		MethodClass1.Mul(200, 100);
		

	}

}
