package MethodArgument_16_11_2025;

public class MethodA_1 {
	
	public static void Add(int A1, int A2) {
		int sum=A1+A2;
		System.out.println(sum);
		
	}
	
	public static void sum(int A3, int A4) {
		int sum=A3-A4;
		System.out.println(sum);
	}
	
	public static void mul(int A5, int A6) {
		int sum=A5*A6;
		System.out.println(sum);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		MethodA_1.Add(200, 300);
		MethodA_1.sum(900, 700);
		MethodA_1.mul(200, 800);

	}

}
