package MethodArgument_22_11_2025;

public class MethodClass_1 {
	
	public static void add(int a1, int a2) {
		int sum=a1+a2;
		System.out.println(sum);
		
	}
	public static void sum(int a3, int a4) {
		int sum= a3-a4;
		System.out.println(sum);
		
	}
	public static void mul(int a5, int a6) {
		int sum=a5*a6;
		System.out.println(sum);
	}
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		MethodClass_1 m1=new MethodClass_1();
		m1.add(3000, 2000);
		m1.sum(1000, 500);
		m1.mul(300, 200);
		
		
		
		

	}

}
