package OopsPackage_16_11_2025;

public class Oops1 {
	
	int id;
	String name;
	int salary;
	
	public void display() {
		System.out.println(id);
		System.out.println(name);
		System.out.println(salary);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Oops1 op=new Oops1();
		op.id=99;
		op.name="Chaitanya";
		op.salary=30000;
		op.display();
		

	}

}
