package Oops_13_12_2025;

public class Oops1 {
	
	int id;
	String name;
	int salary;
	
	public void display() {
		
		System.out.println(id);
		System.out.println(name);
		System.out.println(salary);
		
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Oops1 o1=new Oops1();
		o1.id=99;
		o1.name="AXA";
		o1.salary=80000;
		
		o1.display();

	}

}
