package Oops_21_11_2025;

public class Oops2 {
	
	int id;
	String name;
	int salary;
	
	public void display() {
		System.out.println(id);
		System.out.println(name);
		System.out.println(salary);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Oops2 op2=new Oops2();
		op2.id=22;
		op2.name="Krishna";
		op2.salary=50000;
		
		op2.display();

	}

}
